import { Component, Inject, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { PagamentoDataService } from '../_data-services/pagamento.data-service';

@Component({
  selector: 'app-home',
  templateUrl: './pagamento.component.html',
  styleUrls: ['./pagamento.component.css']
})


export class PagamentoComponent implements OnInit {

  pagamentos: any = [];
  pagamento: any = {};
  codigo: any ;
  showList: boolean = true;


  constructor(private pagamentoDataService: PagamentoDataService) { }

  ngOnInit() {
    this.get();
  }

  get() {
    debugger;
    this.pagamentoDataService.get().subscribe(data => {
      this.pagamentos = data;
      this.showList = true;
    }, error => {
      console.log(error);
      alert('erro interno do sistema');
    })
  }

  save() {
    this.pagamento.codigo = 0
    this.pagamentoDataService.post(this.pagamento).subscribe(data => {
      if (data) {
        alert('Pagamento cadastrado com sucesso');
      } else {
        alert('Erro ao cadastrar Pagamento');
      }
    }, error => {
      console.log(error);
      alert('erro interno do sistema');
    })
  }

  edit(codigo: any) {
    debugger;
    this.pagamentoDataService.put(codigo).subscribe(codigo => {
      if (codigo) {
        alert('Pagamento atualizado com sucesso');
        /*this.get();*/
        this.pagamento = {};
      } else {
        alert('Erro ao atualizar Pagamento');
      }
    }, error => {
      console.log(error);
      alert('erro interno do sistema');
    })
  }

  delete(codigo: any) {
    debugger;
    this.pagamentoDataService.delete(codigo).subscribe(codigo => {
      if (codigo) {
        alert('Pagamento excluído com sucesso');
        /* this.get();*/
        this.pagamento = {};
      } else {
        alert('Erro ao excluir Pagamento');
      }
    }, error => {
      console.log(error);
      alert('erro interno do sistema');
    })
  }

}


