<form>
  <div class="form-group">
    <label>Nome Cliente:</label>
    <input type="text" class="form-control" id="InputNome" placeholder="Nome" [(ngModel)]="pagamento.nome" name="nome">
  </div>
  <div class="form-group">
    <label>Valor:</label>
    <input type="text" class="form-control" id="InputValor" placeholder="Valor" [(ngModel)]="pagamento.valor" name="valor">
  </div>
  <!--<div class="form-group">
    <label>Tipo Pagamento:</label>
    <select class="form-control" id="InputTipoPagamento" placeholder="Selecione o tipo de pagamento" [(ngModel)]="pagamento.tipoPagamento" name="pagamento">
      <option value="1">Boleto</option>
      <option value="2">Pix</option>
    </select>
  </div>-->
  <div class="form-group">
    <label>Linha Digitavel:</label>
    <input type="text" class="form-control" id="InputLinhaDigitavel" placeholder="Linha Digitavel" [(ngModel)]="pagamento.linhaDigitavel" name="linhaDigitavel">
  </div>
  <div class="form-group">
    <label>QRCode:</label>
    <input type="text" class="form-control" id="InputQRCode" placeholder="QRCode" [(ngModel)]="pagamento.qRCode" name="qRCode">
  </div>

  <button type="submit" class="btn btn-primary" (click)="save()">Save</button>
</form>




