import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { HomeDataService } from '../_data-services/home.data-service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})


export class HomeComponent {
  public forecasts: WeatherForecast[] = [];
  public tipoPagamento: any;
  pagamentos: any[] = [];
  pagamento: any = {};
  showList: boolean = true;

  constructor( http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    http.get<WeatherForecast[]>(baseUrl + 'weatherforecast/pix').subscribe(result => {
      this.forecasts = result;
    }, error => console.error(error));
  }


  //save(http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
  //  debugger;
  //  this.homeDataService.post(this.pagamento).subscribe(data => {
  //    if (data) {
  //      alert('Pagamento cadastrado com sucesso');
  //    } else {
  //      alert('Erro ao cadastrar Pagamento');
  //    }
  //  }, error => {
  //    console.log(error);
  //    alert('erro interno do sistema');
  //  })
  //}
}


interface WeatherForecast {
  date: string;
  temperatureC: number;
  temperatureF: number;
  summary: string;
}

<h1>Meus Pagamentos</h1>
<form>

  <div class="form-group">
    <label>Nome Cliente</label>
    <input type="text" class="form-control" name="nomeCliente" />
  </div>

  <div class="form-group">
    <label>Valor</label>
    <input type="text" class="form-control" name="valor" />
  </div>

  <div class="form-group">
    <label>Tipo Pagamento</label>
    <select class="form-control" name="tipoPagamento">
      <option value="1">Boleto</option>
      <option value="2">Pix</option>
    </select>
  </div>

  <div class="form-group" ngif="tipoPagamento === 1">
    <label>Linha Digitável</label>
    <input type="text" class="form-control" name="linhaDigitavel" />
  </div>


  <div class="form-group"  ngif="tipoPagamento === 2">
    <label>QR Code</label>
    <input type="text" class="form-control" name="qrCode" />
  </div>

  <button type="submit" class="btn btn-primary">Salvar</button>
</form>
<table class='table table-striped' aria-labelledby="tableLabel">
  <thead>
    <tr>
      <th>Nome do Cliente</th>
      <th>Valor</th>
      <th>Tipo Pagamento</th>
      <th>Código Pagamento</th>
    </tr>
  </thead>
  <tbody>
    <!--<tr *ngFor="let forecast of forecasts">-->
    <tr>
      <td></td>
      <td></td>
      <td></td>
      <td></td>
    </tr>
  </tbody>
</table>
